import { Button, StyleSheet, Text, TouchableOpacity, View } from 'react-native';
import React, { useState } from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';

const Stack = createBottomTabNavigator();

const HomeScreen = ({ navigation }: any) => {
    return (
        <Button
            title="Go to Jane's profile"
            onPress={() => navigation.navigate('Profile', { name: 'Jane' })}
        />
    );
};
const ProfileScreen = ({ navigation, route }: any) => {
    return <Text>This is {route.params.name}'s profile</Text>;
};

const MainComponent = () => {
    const [click, setClick] = useState(true);
    const onPress = () => setClick(!click);
    return (
        // <View
        //     style={{
        //         backgroundColor: '#000',
        //         alignItems: 'center',
        //         justifyContent: 'center',
        //         flex: 1,
        //     }}
        // >
        //     <Text style={{ color: '#fff', fontSize: 50, textAlign: 'center' }}>Phần Nguyễn</Text>
        //     <Text style={{ color: 'aqua', fontSize: 20, textAlign: 'center' }}>
        //         {click ? 'Dễ thương' : 'Đẹp trai'}
        //     </Text>
        //     <TouchableOpacity style={styles.button} onPress={onPress}>
        //         <Text>{click ? 'Click here' : 'Clicked'}</Text>
        //     </TouchableOpacity>
        // </View>
        <NavigationContainer>
            <Stack.Navigator>
                <Stack.Screen name="Home" component={HomeScreen} />
                <Stack.Screen name="Profile" component={ProfileScreen} />
            </Stack.Navigator>
        </NavigationContainer>
    );
};

export default MainComponent;

const styles = StyleSheet.create({
    container: {
        flex: 1,
        justifyContent: 'center',
        paddingHorizontal: 10,
    },
    button: {
        alignItems: 'center',
        backgroundColor: '#DDDDDD',
        padding: 10,
        mt: 10,
    },
    countContainer: {
        alignItems: 'center',
        padding: 10,
    },
});
