import { Button, StyleSheet, Text, TouchableOpacity, View } from 'react-native';
import React, { useState } from 'react';

const MainComponent = () => {
    const [click, setClick] = useState(true);
    const onPress = () => setClick(!click);
    return (
        <View
            style={{
                backgroundColor: '#000',
                alignItems: 'center',
                justifyContent: 'center',
                flex: 1,
            }}
        >
            <Text style={{ color: '#fff', fontSize: 50, textAlign: 'center' }}>Phần Nguyễn</Text>
            <Text style={{ color: 'aqua', fontSize: 20, textAlign: 'center' }}>Cute</Text>
            <TouchableOpacity style={styles.button} onPress={onPress}>
                <Text>{click ? 'Click here' : 'Clicked'}</Text>
            </TouchableOpacity>
        </View>
    );
};

export default MainComponent;

const styles = StyleSheet.create({
    container: {
        flex: 1,
        justifyContent: 'center',
        paddingHorizontal: 10,
    },
    button: {
        alignItems: 'center',
        backgroundColor: '#DDDDDD',
        padding: 10,
    },
    countContainer: {
        alignItems: 'center',
        padding: 10,
    },
});
