import { Button, StyleSheet, Text, View } from 'react-native';
import React, { useState } from 'react';

const MainComponent = () => {
    const [click, setClick] = useState('Click Here');
    return (
        <View
            style={{
                backgroundColor: '#000',
                alignItems: 'center',
                justifyContent: 'center',
                flex: 1,
            }}
        >
            <Text style={{ color: '#fff', fontSize: 50, textAlign: 'center' }}>Phần Nguyễn</Text>
            <Text style={{ color: 'aqua', fontSize: 20, textAlign: 'center' }}>Cute</Text>
            <Button
                // style={{ color: 'aqua', fontSize: 20, textAlign: 'center' }}
                title={click}
                color={'aqua'}
                onPress={() => setClick('Clicked')}
            />
        </View>
    );
};

export default MainComponent;

const styles = StyleSheet.create({});
