import {
    Button,
    Dimensions,
    StyleSheet,
    Text,
    TextInput,
    TouchableOpacity,
    View,
} from 'react-native';
import React, { useState } from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import Ionicons from 'react-native-vector-icons/Ionicons';
import AntDesign from 'react-native-vector-icons/AntDesign';
import { MAIN } from 'src/contant';

const Stack = createBottomTabNavigator();

const HomeScreen = ({ navigation }: any) => {
    return (
        <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
            <Text>Home login!</Text>
            <Button
                title="click he2re"
                onPress={() => navigation.navigate(MAIN, { screen: 'Home' })}
            ></Button>
        </View>
    );
};
const ProfileScreen = () => {
    return (
        <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
            <Text>Settings! login</Text>
        </View>
    );
};
const SettingScreen = () => {
    return (
        <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
            <Text>Settings! login</Text>
        </View>
    );
};
const NotificationScreen = () => {
    return (
        <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
            <Text>Notification! login</Text>
        </View>
    );
};

const LoginComponent = ({ navigation }: any) => {
    const [click, setClick] = useState(true);
    const onPress = () => setClick(!click);
    return (
        <View style={styles.container}>
            <Text style={styles.title}>Login React-Native</Text>
            <TextInput
                placeholder="Username"
                placeholderTextColor="black"
                underlineColorAndroid="transparent"
                style={styles.txtInput}
                onChangeText={(username) => console.log(username)}
            />
            <TextInput
                placeholder="Password"
                underlineColorAndroid="transparent"
                placeholderTextColor="black"
                secureTextEntry={true}
                style={styles.txtInput}
                onChangeText={(password) => console.log(password)}
            />
            <TouchableOpacity style={styles.btnLogin}>
                <Text style={styles.txtLogin}>Login</Text>
            </TouchableOpacity>
        </View>
        // <Stack.Navigator
        //     screenOptions={({ route }) => ({
        //         tabBarIcon: ({ focused, color, size }) => {
        //             let iconName = '';

        //             if (route.name === 'Ho') {
        //                 iconName = 'home';
        //             } else if (route.name === 'Se') {
        //                 iconName = 'setting';
        //             } else if (route.name === 'Pro') {
        //                 iconName = 'profile';
        //             } else {
        //                 iconName = 'notification';
        //             }

        //             // You can return any component that you like here!
        //             return <AntDesign name={iconName} size={size} color={color} />;
        //         },
        //         tabBarActiveTintColor: 'tomato',
        //         tabBarInactiveTintColor: 'gray',
        //     })}
        // >
        //     <Stack.Screen name="Ho" component={HomeScreen} />
        //     <Stack.Screen name="No" component={NotificationScreen} />
        //     <Stack.Screen name="Se" component={SettingScreen} />
        //     <Stack.Screen name="Pro" component={ProfileScreen} />
        // </Stack.Navigator>
    );
};

export default LoginComponent;
const DEVICE_WIDTH = Dimensions.get('screen').width;
const DEVICE_HEIGHT = Dimensions.get('screen').height;
const styles = StyleSheet.create({
    container: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
        backgroundColor: '#F5FCFF',
    },
    title: {
        fontSize: 30,
        color: 'red',
    },
    txtInput: {
        backgroundColor: 'rgba(0,0,0, 0.1)',
        width: DEVICE_WIDTH - 40,

        marginHorizontal: 20,
        padding: 8,
        borderRadius: 20,
        color: '#000',
        marginTop: 2,
    },
    btnLogin: {
        width: DEVICE_WIDTH - 40,
        backgroundColor: 'rgba(0,145,234,1)',
        padding: 8,
        borderRadius: 20,
        marginTop: 2,
    },
    txtLogin: {
        color: '#fff',
        textAlign: 'center',
    },
});
